import React,{useState,useEffect} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";


const ComparePage=()=>{
    const{product}=useParams();   //get product from the url
    const[data,setData]=useState([]);     // store the data in state
    const[loading,setLoading]=useState(true); 
    const [error, setError] = useState(null);   //show product is loading 



    //fetch data using useeffect
    useEffect=(()=>{
        const FetchPrices=async()=>{
            try {
                setLoading(true);
                const res = await axios.get(`http://localhost:5000/compare?product=${product}`);  //hit backend api
                setData[res.data]; //now dta come in frontend state  store in react
            } catch (err) {
                console.error("Error in Fetching data" ,err);
                setError("Failed to load data");
            }finally{
                setLoading(false);
            }
        };
        FetchPrices();
    },[product]);

    if(loading) return <p className="text-center mt-4"> Loading ...</p>
    if (error) {
      return <div className="text-center mt-10 text-red-600">‚ùå {error}</div>;
    }

    return (
      <div className="p-4 max-w-2xl mx-auto">
        <h2 className="text-xl font-bold mb-4 text-center ">
          Results for :<span className="text-blue-600">{product}</span>
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6"></div>
        {data.map((item, index) => {
          <div
            key={index}
            className="border p-4 rounded mb-4 bg-white shadow-md hover:shadow-xl transition"
          >
            <h3 className="text-lg font-semibold mb-2">{item.source}</h3>
            <p className="text-gray-700 font-medium mb-1">{item.title}</p>
            <p className="text-green-600 font-bold">{item.price}</p>
            <a
              href={item.link}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-block bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
            >
              View Product
            </a>
          </div>;
        })}
      </div>
    );
};

export default ComparePage;
